CREATE TABLE IF NOT EXISTS user (id MEDIUMINT NOT NULL AUTO_INCREMENT,name CHAR(30) NOT NULL,hashed_password CHAR(250) NOT NULL,salt CHAR(250) NOT NULL,PRIMARY KEY (id),UNIQUE INDEX(name));
CREATE TABLE IF NOT EXISTS wallet (id MEDIUMINT NOT NULL AUTO_INCREMENT,user_id INT UNSIGNED NOT NULL ,network_type CHAR(30) NOT NULL,network_address CHAR(250) NOT NULL,amount INT DEFAULT 0 NOT NULL,PRIMARY KEY (id));